CREATE PROCEDURE [Camp].[InsertRoleSP]
    @role_name NVARCHAR(50),
    @description NVARCHAR(255)
AS

BEGIN SET NOCOUNT ON;
--Inserting Department
MERGE Camp.Department AS Target USING dbo.Employee As Source ON Target.name = Source.Department WHEN NOT MATCHED THEN INSERT ([name]) Values (source.Department);
-- Inserting Designation MERGE Camp.Designation AS Target USING dbo.Employee As Source ON Target.title = Source.Designation WHEN NOT MATCHED THEN INSERT (title) Values (source.Designation);
-- CTE to prepare the staging data with department and designation information WITH StagingData AS ( SELECT se.Emp_id, se.[full_name], se.[Email] AS email, dp.department_id, ds.designation_id, us.user_id AS manager_id, [last_login_date], --CASE WHEN se.[full_name] = se.[Manager] THEN 0 ELSE 0 END AS is_self_manager, se.[last_updated_date] FROM dbo.employee se JOIN Camp.Department dp ON se.[Department] = dp.name JOIN Camp.Designation ds ON se.Designation = ds.title LEFT JOIN Camp.[User] us ON se.Manager = us.full_name )
-- Merge data from StagingData into User table MERGE Camp.[User] AS target USING StagingData AS source ON target.emp_id = source.emp_id WHEN NOT MATCHED THEN INSERT ( [emp_id], [full_name], [email], [department_id], [designation_id], [manager_id], [last_login_date], [last_updated_date] ) VALUES ( source.emp_id, source.full_name, source.email, source.department_id, source.designation_id, source.Manager_id, --CASE WHEN source.is_self_manager = 1 THEN NULL ELSE source.manager_id END, source.[last_login_date], GETDATE() ) WHEN MATCHED THEN UPDATE SET target.full_name = source.full_name, target.email = source.email, target.department_id = source.department_id, target.designation_id = source.designation_id, target.manager_id = source.manager_id, -- target.manager_id = CASE WHEN source.is_self_manager = 1 THEN NULL ELSE source.manager_id END, target.last_login_date = source.[last_login_date], target.last_updated_date = GETDATE() WHEN NOT MATCHED BY SOURCE THEN DELETE ;
END

BEGIN SET NOCOUNT ON;
DECLARE @current_date DATETIME = SWITCHOFFSET(GETDATE(), '+05:30'); DECLARE @app_id INT; DECLARE @is_default BIT;
SELECT @is_default = CASE WHEN @Section_id = 2 THEN 1 ELSE 0 END FROM Camp.App_Catalog
MERGE INTO Camp.App_Catalog AS t USING ( SELECT @appname AS [app_name], @iconurl AS icon_url, @appurl AS app_url, @description AS [description], @is_default AS is_default, @current_date AS last_updated_date, 1 AS is_active, @Section_id AS section_id ) AS s
ON t.[app_name] =s.[app_name] AND t.app_url = s.app_url
WHEN NOT MATCHED THEN INSERT ( [app_name], icon_url, app_url, [description], is_default, last_updated_date, is_active, section_id ) VALUES ( s.[app_name], s.icon_url, s.app_url, s.[description], s.is_default, s.last_updated_date, s.is_active, s.section_id ) WHEN MATCHED THEN UPDATE SET t.icon_url = s.icon_url, t.app_url = s.app_url, t.[description] = s.[description], t.is_default = s.is_default, t.last_updated_date = s.last_updated_date, t.section_id = s.section_id;
END;

BEGIN
	SET NOCOUNT ON; 

		DECLARE 
				@comments VARCHAR(1000),
				@app_name VARCHAR(100),
				@name VARCHAR(100),
				@action VARCHAR(30),
				@date DATETIME = SWITCHOFFSET(GETDATE(), '+05:30');


		SELECT @name = full_name 
		FROM camp.[user]
		WHERE [user_id] = @user_id;

		SELECT @action = action_type 
		FROM camp.app_action_type
		WHERE action_type_id = @action_id;

		SELECT @app_name = app_name 
		FROM camp.app_catalog
		WHERE app_id = @app_id;

		SET @comments = CONCAT(@app_name, ' has been ', LOWER(@action), ' by ', @name);

		INSERT INTO Camp.App_Action_Log
					(
						app_id, 
						action_by, 
						action_type_id, 
						action_date, 
						comments
					)
		VALUES
					(
						@app_id, 
						@user_id,
						@action_id, 
						@date, 
						@comments
					);

END;

