CREATE PROCEDURE [Camp].[usp_GetAndMarkUserNotifications]
    @UserID INT
AS

BEGIN -- Declare a table variable to hold the result DECLARE @RecentNotification TABLE ( notification_id INT, Message NVARCHAR(MAX), is_read BIT, created_at DATETIME );
-- Select the most recent notification for the specified user INSERT INTO @RecentNotification SELECT TOP 1 notification_id, Message, is_read, created_at FROM Notifications WHERE user_id = @UserID ORDER BY created_at DESC;
-- Update all notifications as read (1) for the specified user UPDATE Notifications SET is_read = 1 WHERE user_id = @UserID;
-- Return the most recent notification SELECT * FROM @RecentNotification; END;

CREATE PROCEDURE [Camp].[usp_GetUserNotifications]
    @UserID INT
AS

BEGIN 
	SET NOCOUNT ON;

		SELECT r.role_name AS [UserRole]
		FROM Camp.[User] AS u

		JOIN Camp.User_Role_Mapping AS ur
		ON u.[user_id] = ur.[user_id]

		JOIN Camp.[Role] AS r
		ON r.role_id = ur.role_id

		WHERE u.email = @email;
END;

BEGIN
	SET NOCOUNT ON;
	DECLARE @date DATETIME = SWITCHOFFSET(GETDATE(), '+05:30');

		UPDATE Camp.App_Catalog
		SET section_id = @section_id,
			last_updated_date = @date
		WHERE app_id = @app_id
END;

BEGIN
    SET NOCOUNT ON;

		DECLARE @date DATETIME = SWITCHOFFSET(GETDATE(), '+05:30');

		UPDATE Camp.User_Login
		SET is_reported = 1
		WHERE login_id = @login_id;

		INSERT INTO Camp.Reported_login 
			(
				login_id, 
				reported_by, 
				reported_reason, 
				reported_date
			)
		VALUES 
			(
				@login_id,
				@reported_by, 
				@reported_reason,
				@date
			);

END;

CREATE PROCEDURE Camp.usp_ReportUserLogin
    @login_id INT,
    @reported_by INT,
    @reported_reason VARCHAR(2000)
AS

BEGIN
	SET NOCOUNT ON;
		DECLARE
				@date DATETIME = SWITCHOFFSET(GETDATE(), '+05:30'),
				@sectionid INT;

		SELECT @sectionid = section_id 
		FROM Camp.Section
		WHERE section_name = @section_name;

		-- Handle INSERT/UPDATE or DEACTIVATE based on @is_deleted flag
		IF @is_deleted = 0
		BEGIN
			MERGE INTO Camp.Section AS t
			USING (
				SELECT 
					@section_name	AS section_name,
					1				AS is_active,
					@date			AS last_updated_date
			) AS s
			ON t.section_name = s.section_name
			WHEN MATCHED THEN 
				UPDATE 
				SET t.is_active			= s.is_active,
					t.last_updated_date = s.last_updated_date
			WHEN NOT MATCHED THEN 
				INSERT	(
							section_name, 
							is_active, 
							last_updated_date
						)
				VALUES	(
							s.section_name,
							s.is_active, 
							s.last_updated_date
						);
		END
		ELSE IF @is_deleted = 1
		BEGIN 
			UPDATE Camp.Section
			SET is_active = 0,
				last_updated_date = @date
			WHERE section_id = @sectionid;

			-- Reassign apps from the deleted section to a Others section
			UPDATE Camp.App_Catalog
			SET section_id = 3, -- section_id 3 is the 'Others' section
				last_updated_date = @date
			WHERE section_id = @sectionid;
		END;
END;

BEGIN DECLARE @date DATETIME = SWITCHOFFSET(GETDATE(), '+05:30');
SET NOCOUNT ON; IF @is_listed = 1 BEGIN -- Add or update application in the user's list MERGE INTO Camp.User_AppSection_Mapping AS t USING ( SELECT @user_id AS user_id, @app_id AS app_id, 1 AS is_active, @date AS last_updated_date ) AS s ON t.user_id = s.user_id AND t.app_id = s.app_id
WHEN NOT MATCHED THEN INSERT ( user_id, app_id, is_active, last_updated_date ) VALUES ( s.user_id, s.app_id, s.is_active, s.last_updated_date ) WHEN MATCHED THEN UPDATE SET t.is_active = s.is_active, t.last_updated_date = s.last_updated_date; END ELSE IF @is_listed = 0 BEGIN -- Remove application from the user's list UPDATE Camp.User_AppSection_Mapping SET is_active = 0, last_updated_date = @date WHERE user_id = @user_id AND app_id = @app_id; END; END;

CREATE PROCEDURE [Camp].[usp_SaveUserAppSection]
@user_id INT,
@app_id INT ,
@is_listed BIT
AS

BEGIN
    -- Update all notifications to unread (0) for the specified user
    UPDATE Notifications
    SET is_read = 0
    WHERE user_id = @UserID;
END;

