CREATE PROCEDURE [Camp].[InsertRoleSP]
    @role_name NVARCHAR(50),
    @description NVARCHAR(255)
AS

BEGIN SET NOCOUNT ON;
--Inserting Department
MERGE Camp.Department AS Target USING dbo.Employee As Source ON Target.name = Source.Department WHEN NOT MATCHED THEN INSERT ([name]) Values (source.Department);
-- Inserting Designation MERGE Camp.Designation AS Target USING dbo.Employee As Source ON Target.title = Source.Designation WHEN NOT MATCHED THEN INSERT (title) Values (source.Designation);
-- CTE to prepare the staging data with department and designation information WITH StagingData AS ( SELECT se.Emp_id, se.[full_name], se.[Email] AS email, dp.department_id, ds.designation_id, us.user_id AS manager_id, [last_login_date], --CASE WHEN se.[full_name] = se.[Manager] THEN 0 ELSE 0 END AS is_self_manager, se.[last_updated_date] FROM dbo.employee se JOIN Camp.Department dp ON se.[Department] = dp.name JOIN Camp.Designation ds ON se.Designation = ds.title LEFT JOIN Camp.[User] us ON se.Manager = us.full_name )
-- Merge data from StagingData into User table MERGE Camp.[User] AS target USING StagingData AS source ON target.emp_id = source.emp_id WHEN NOT MATCHED THEN INSERT ( [emp_id], [full_name], [email], [department_id], [designation_id], [manager_id], [last_login_date], [last_updated_date] ) VALUES ( source.emp_id, source.full_name, source.email, source.department_id, source.designation_id, source.Manager_id, --CASE WHEN source.is_self_manager = 1 THEN NULL ELSE source.manager_id END, source.[last_login_date], GETDATE() ) WHEN MATCHED THEN UPDATE SET target.full_name = source.full_name, target.email = source.email, target.department_id = source.department_id, target.designation_id = source.designation_id, target.manager_id = source.manager_id, -- target.manager_id = CASE WHEN source.is_self_manager = 1 THEN NULL ELSE source.manager_id END, target.last_login_date = source.[last_login_date], target.last_updated_date = GETDATE() WHEN NOT MATCHED BY SOURCE THEN DELETE ;
END

BEGIN SET NOCOUNT ON;-- This helps prevent extra result sets from interfering with SELECT statements.
DECLARE @current_date DATETIME = GETDATE(); DECLARE @app_id INT; DECLARE @sectionID_Internal INT = NULL
SELECT @sectionID_Internal = [section_id] FROM Camp.Section WHERE [section_name] = 'Arus Internals'
SET @sectionID_Internal = ISNULL(@sectionID_Internal, 0)
-- Check if the app already exists IF NOT EXISTS ( SELECT 1 FROM [Camp].[App_Catalog] WHERE [app_name] = @appname ) BEGIN -- Insert new app into App_Catalog INSERT INTO [Camp].[App_Catalog] ( [app_name] ,[icon_url] ,[app_url] ,[description] ,[is_default] ,[last_updated_date] ) VALUES ( @appname ,@iconurl ,@appurl ,@description ,IIF(@sectionID_Internal = @Section_id, 1, 0) ,@current_date );
SELECT @app_id = [app_id] FROM [Camp].[App_Catalog] WHERE [app_name] = @appname
-- Update existing mapping in App_Section_Mapping MERGE INTO [Camp].[App_Section_Mapping] AS TARGET USING ( SELECT @app_id AS [appid] ,@section_id AS [sectionid] ,1 AS [isactive] ) AS SOURCE ON TARGET.[app_id] = SOURCE.[appid] WHEN MATCHED THEN UPDATE SET TARGET.[section_id] = SOURCE.[sectionid] ,TARGET.[last_updated_date] = @current_date WHEN NOT MATCHED THEN INSERT ( [section_id] ,[app_id] ,[last_updated_date] ,[is_active] ) VALUES ( SOURCE.[sectionid] ,SOURCE.[appid] ,@current_date ,SOURCE.[isactive] );
SET @result = 1 END ELSE BEGIN SET @result = - 1
RAISERROR ( 'Appname already exists. Please enter new appname..!' ,16 ,1 ); END END;

CREATE PROCEDURE [Camp].[usp_GetAndMarkUserNotifications]
    @UserID INT
AS

BEGIN --DECLARE @User_Id INT = 37; --DECLARE @isdelete bit =0; SELECT ASM.section_app_id ,U.[user_id] ,AC.[app_id] ,AC.[app_name] ,AC.icon_url ,AC.[description] ,@isdelete as isdelete , CASE WHEN UAM.user_appsection_id IS NULL AND AC.is_default = 0 THEN 1 ELSE 0 END AS [is_active] --,IIF((UAM.user_appsection_id IS NULL, 1, 0) OR (AC.is_default = 1,0,1)) AS [is_active]
--, UAM.* FROM [camp].[App_Catalog] AC WITH (NOLOCK) JOIN ( SELECT * FROM [camp].[User] WITH (NOLOCK) WHERE [user_id] = @User_Id ) U ON 1 = 1 INNER JOIN [camp].[App_Section_Mapping] ASM ON ASM.[app_id] = AC.[app_id] LEFT JOIN [Camp].[User_AppSection_Mapping] UAM ON UAM.[section_app_id] = ASM.[section_app_id] AND UAM.[user_id] = U.[user_id] Where ASM.[is_active] = 1;
END;

BEGIN -- Declare a table variable to hold the result DECLARE @RecentNotification TABLE ( notification_id INT, Message NVARCHAR(MAX), is_read BIT, created_at DATETIME );
-- Select the most recent notification for the specified user INSERT INTO @RecentNotification SELECT TOP 1 notification_id, Message, is_read, created_at FROM Notifications WHERE user_id = @UserID ORDER BY created_at DESC;
-- Update all notifications as read (1) for the specified user UPDATE Notifications SET is_read = 1 WHERE user_id = @UserID;
-- Return the most recent notification SELECT * FROM @RecentNotification; END;

CREATE PROCEDURE [Camp].[usp_GetUserNotifications]
    @UserID INT
AS

BEGIN DECLARE @Userid INT;
-- Retrieve the user_id based on the provided email SELECT @Userid = [user_id] FROM [camp].[User] WHERE email = @Email;
IF @Userid IS NOT NULL BEGIN -- User details with department, designation, and admin role SELECT U.[user_id] AS [UserID] ,U.emp_id AS [EmployeeID] ,U.full_name AS [EmployeeName] ,U.email AS [Email] ,M.email AS [ManagerEmail] ,M.emp_id AS [ManagerID] ,M.[full_name] AS [ManagerName] ,DP.[name] AS [DepartmentName] ,DG.[title] AS [Designation] ,IIF(R.[role_name] IS NOT NULL, 1, 0) AS [IsAdmin] FROM camp.[User] U WITH (NOLOCK) INNER JOIN Camp.[Department] DP WITH (NOLOCK) ON DP.[department_id] = U.[department_id] INNER JOIN Camp.[Designation] DG WITH (NOLOCK) ON DG.[designation_id] = U.[designation_id] LEFT JOIN Camp.[User] M WITH (NOLOCK) ON U.[manager_id] = M.[user_id] LEFT JOIN Camp.[User_Role_Mapping] URM WITH (NOLOCK) ON U.[user_id] = URM.[user_id] LEFT JOIN Camp.[Role] R WITH (NOLOCK) ON R.[role_id] = URM.[role_id] AND R.[role_name] = 'Admin' WHERE U.[user_id] = @Userid;
SET @result = 1; --Section details with Status SELECT [section_id] ,[section_name] ,[is_active] FROM [Camp].[Section] END ELSE BEGIN SET @result = - 1; END
-- Output the result SELECT @result AS result; END

CREATE PROCEDURE [Camp].[usp_LoginCheck]
/*
Author: Anushka Parasuraman
Created Date: 08-Jul-2024
About SP: Fetching User & Section details to Login Page

Sample Execution:

DECLARE @Email VARCHAR(50) = 'anushka.p@arus.co.in';
DECLARE @result SMALLINT;

EXEC [Camp].[usp_LoginCheck] @Email, @result OUTPUT;

*/
/* 
SP Change History Capture
-------------------------------------------------------------------
Date			ChangeBy		Reason
09-07-2024		Anushka P		Initial Write

-------------------------------------------------------------------
*/
   @Email VARCHAR(50)
	,@result SMALLINT OUT
AS

BEGIN DECLARE @newsectionid INT; DECLARE @appid INT; DECLARE @currentsectionid INT; DECLARE @section_app_id INT; DECLARE @new_section_app_id INT;
-- Get the section_id of the new section SELECT @newsectionid = section_id FROM [Camp].[Section] WHERE section_name = @sectionname;
-- Retrieve app_id if app exists IF EXISTS (SELECT 1 FROM [Camp].[App_Catalog] WHERE [app_name] = @appname) BEGIN SELECT @appid = [app_id] FROM [Camp].[App_Catalog] WHERE [app_name] = @appname; END
-- Get the current section_id for the given app SELECT @currentsectionid = section_id FROM [Camp].[App_Section_Mapping] WHERE app_id = @appid AND is_active = 1;
-- Deactivate the current mapping if it exists IF @currentsectionid IS NOT NULL BEGIN UPDATE [Camp].[App_Section_Mapping] SET is_active = 0 WHERE section_id = @currentsectionid AND app_id = @appid; END
-- Check if the new section_id and app_id combination exists in App_Section_Mapping SELECT @section_app_id = section_app_id FROM [Camp].[App_Section_Mapping] WHERE section_id = @newsectionid AND app_id = @appid;
-- If the combination does not exist, create a new mapping IF @section_app_id IS NULL BEGIN INSERT INTO [Camp].[App_Section_Mapping] (section_id, app_id, last_updated_date, is_active) VALUES (@newsectionid, @appid, GETDATE(), 1); -- Assuming 1 means active
-- Retrieve the newly created section_app_id SELECT @new_section_app_id = section_app_id FROM [Camp].[App_Section_Mapping] WHERE section_id = @newsectionid AND app_id = @appid AND is_active = 1; END ELSE BEGIN -- Update the existing mapping to active UPDATE [Camp].[App_Section_Mapping] SET is_active = 1, last_updated_date = GETDATE() WHERE section_app_id = @section_app_id;
-- Set @new_section_app_id to the existing section_app_id SET @new_section_app_id = @section_app_id; END
-- If the new section is inactive, make it active IF EXISTS (SELECT 1 FROM [Camp].[Section] WHERE section_id = @newsectionid AND is_active = 0) BEGIN UPDATE [Camp].[Section] SET is_active = 1 WHERE section_id = @newsectionid; END
-- Update User_AppSection_Mapping to reflect the new section_app_id UPDATE [Camp].[User_AppSection_Mapping] SET section_app_id = @new_section_app_id, last_updated_date = GETDATE() WHERE section_app_id IN ( SELECT section_app_id FROM [Camp].[App_Section_Mapping] WHERE app_id = @appid AND section_id = @currentsectionid );
-- If the current section becomes inactive due to no active app, make it inactive IF NOT EXISTS ( SELECT 1 FROM [Camp].[App_Section_Mapping] WHERE section_id = @currentsectionid AND is_active = 1 ) BEGIN UPDATE [Camp].[Section] SET is_active = 0 WHERE section_id = @currentsectionid; END END;

CREATE PROCEDURE [Camp].[usp_MoveToSection]
    @sectionname VARCHAR(50),
    @appname VARCHAR(50)
AS

BEGIN SET NOCOUNT ON;
DECLARE @current_date DATETIME = GETDATE();
-- Perform the action based on the input parameter @IsDelete IF @IsDelete = 0 BEGIN
IF EXISTS ( SELECT 1 FROM [Camp].[Section] WHERE section_name = @sectionname AND is_active = 0 ) BEGIN UPDATE [Camp].[Section] SET is_active = 1 ,last_updated_date = @current_date WHERE section_name = @sectionname;
SET @Result = 2 END ELSE IF NOT EXISTS ( SELECT 1 FROM [Camp].[Section] WHERE section_name = @sectionname ) BEGIN INSERT INTO [Camp].[Section] ( section_name ,is_active ,last_updated_date ) VALUES ( @sectionname ,1 ,@current_date );
SET @Result = 1 END END ELSE IF @IsDelete = 1 BEGIN -- Set is_active to 0 for the section in Camp.Section UPDATE [Camp].[Section] SET is_active = 0 ,last_updated_date = @current_date WHERE section_id = @sectionid;
-- Delete the section mapping DELETE FROM [Camp].[User_AppSection_Mapping] WHERE section_app_id IN ( SELECT [section_app_id] FROM [Camp].[App_Section_Mapping] WHERE section_id =@sectionid );
SET @Result = 3 END ELSE BEGIN SET @Result = - 1 END; END;

BEGIN SET NOCOUNT ON; DECLARE @current_date DATETIME = GETDATE();
IF @IsDelete = 0 BEGIN -- Insert or update the User_AppSection table for the specific user MERGE INTO [Camp].[User_AppSection_Mapping] AS target USING ( SELECT @userid AS [User_Id], @section_app_id AS section_app_id ) AS source ON ( target.[User_Id] = source.[User_Id] AND target.section_app_id = source.section_app_id ) WHEN NOT MATCHED THEN INSERT ( [User_Id], section_app_id, is_active, last_updated_date ) VALUES ( source.[User_Id], source.section_app_id, 1, GETDATE() ) WHEN MATCHED AND target.is_active = 0 THEN UPDATE SET target.is_active = 1, target.last_updated_date = GETDATE(); END; ELSE IF @IsDelete = 1 BEGIN -- Set is_active to 0 for the specific user and section UPDATE [Camp].[User_AppSection_Mapping] SET is_active = 0, last_updated_date = @current_date WHERE [User_Id] = @userid AND section_app_id = @section_app_id;
-- Optionally, delete the specific user-section mapping if needed DELETE FROM [Camp].[User_AppSection_Mapping] WHERE [User_Id] = @userid AND section_app_id = @section_app_id;
END; END;

CREATE PROCEDURE [Camp].[usp_SaveUserAppSection] (
	@userid INT,
	@section_app_id INT,
	@IsDelete BIT = 0
)
AS

BEGIN
    -- Update all notifications to unread (0) for the specified user
    UPDATE Notifications
    SET is_read = 0
    WHERE user_id = @UserID;
END;

